import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Components
import Header from './components/Header';
import Dashboard from './pages/Dashboard';
import AddTransaction from './pages/AddTransaction';
import EditTransaction from './pages/EditTransaction';
import TransactionDetails from './pages/TransactionDetails';
import Login from './pages/Login';
import Register from './pages/Register';

const App = () => {
  // TODO: Implement actual authentication check
  const isAuthenticated = false; // This should be replaced with actual auth check

  return (
    <>
      {isAuthenticated && <Header />}
      <div className="container">
        <Routes>
          <Route
            path="/"
            element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" />}
          />
          <Route
            path="/add-transaction"
            element={isAuthenticated ? <AddTransaction /> : <Navigate to="/login" />}
          />
          <Route
            path="/edit-transaction/:id"
            element={isAuthenticated ? <EditTransaction /> : <Navigate to="/login" />}
          />
          <Route
            path="/transaction/:id"
            element={isAuthenticated ? <TransactionDetails /> : <Navigate to="/login" />}
          />
          <Route
            path="/login"
            element={!isAuthenticated ? <Login /> : <Navigate to="/" />}
          />
          <Route
            path="/register"
            element={!isAuthenticated ? <Register /> : <Navigate to="/" />}
          />
        </Routes>
      </div>
      <ToastContainer position="bottom-right" />
    </>
  );
};

export default App;
